function displayQuestion(index) {
    updateShuffleChoiceState();
    const q = quizQuestions[index];
    let isAnswered = userAnswers[index] && (Array.isArray(userAnswers[index]) ? userAnswers[index].length > 0 : userAnswers[index] !== "");
    
    let html = `
        <div class="question-header">
            <p class="no-zoom"><strong>Question ${index + 1}</strong></p>
        </div>
    `;

    // Render question based on type

    if (q.type === 'TF') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        html += `<h2>${q.question}</h2>`;
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls controlsList="nodownload">
                        <source src="${q.audio}" type="audio/mpeg">
                        Your device does not support the audio element.
                    </audio>
                </div>
                `;
        }
        html += q.choices.map(choice => `
            <div class="choice">
                <label><input type="radio" name="answer_${index}" value="${choice}" 
                ${userAnswers[index] === choice ? 'checked' : ''} 
                onchange="saveAnswer(${index}, this)"> ${choice}</label>
            </div>
        `).join('');
    } else if (q.type === 'Information') {
        html += `<h1>${q.section}</h1>`;
        html += `<h2>${q.instruction}</h2>`;
    }else if (q.type === 'MCQ') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        html += `<h2>${q.question}</h2>`;
    
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
    
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls>
                        <source src="${q.audio}" type="audio/mpeg">
                        Your browser does not support the audio element.
                    </audio>
                </div>
            `;
        }
    
        const inputType = q.multiple ? 'checkbox' : 'radio';
        let choices = q.choices;
    
        if (shuffleChoiceEnabled) {
            choices = shuffleChoiceArray([...choices]); // Shuffle a copy
        }
    
        html += choices.map((choice, i) => {
            const choiceValue = choice.text || choice;
            const isChecked = q.multiple 
                ? (userAnswers[index] && userAnswers[index].includes(choiceValue)) 
                : (userAnswers[index] === choiceValue);
            return `
                <div class="choice">
                    <label>
                        <input type="${inputType}" name="answer_${index}" value="${choiceValue}" 
                        ${isChecked ? 'checked' : ''} 
                        onchange="saveAnswer(${index}, this)"> ${choiceValue}
                        ${choice.image ? `<img src="${choice.image}" alt="${choice.text}"
                            class="choice-image">` : ''}
                    </label>
                </div>
            `;
        }).join('');
    } else if (q.type === 'MWQ') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls>
                        <source src="${q.audio}" type="audio/mpeg">
                        Your browser does not support the audio element.
                    </audio>
                </div>
                `;
        }
        let mwqContent = '';
        let parts = q.question.split(/\[(\d+)\]/);
        mwqContent += parts.map((part, i) => {
            if (i % 2 === 1) {
                let blank = q.blanks.find(b => b.position == part);
                const blankIndex = parseInt(part) - 1;
                const selectedValue = userAnswers[index]?.[blankIndex] || '';
                const displayValue = selectedValue.replace(/^\$|\$/g, '');
                if (currentSubject === 'Mathematics') {
                    return `
                    <div class="mwq-dropdown" data-blank-index="${blankIndex}">
                        <div class="mwq-selected" onclick="toggleMWQDropdown(${index}, ${blankIndex})">
                            <span class="mwq-selected-text">${displayValue ? `\\(${displayValue}\\)` : 'Select'}</span>
                        </div>
                        <div class="mwq-options" id="mwq-options_${index}_${blankIndex}" style="display: none;">
                            <div class="mwq-option" onclick="selectMWQOption(${index}, ${blankIndex}, '')">Select</div>
                            ${blank.options.map(opt => {
                                const cleanOpt = opt.replace(/^\$|\$/g, '');
                                return `
                                    <div class="mwq-option" onclick="selectMWQOption(${index}, ${blankIndex}, '${opt}')">
                                        \\(${cleanOpt}\\)
                                    </div>
                                `;
                            }).join('')}
                        </div>
                    </div>
                    `;
                } else {
                    return `
                    <div class="mwq-dropdown" data-blank-index="${blankIndex}">
                        <div class="mwq-selected" onclick="toggleMWQDropdown(${index}, ${blankIndex})">
                            <span class="mwq-selected-text">${displayValue ? `${displayValue}` : 'Select'}</span>
                        </div>
                        <div class="mwq-options" id="mwq-options_${index}_${blankIndex}" style="display: none;">
                            <div class="mwq-option" onclick="selectMWQOption(${index}, ${blankIndex}, '')">Select</div>
                            ${blank.options.map(opt => {
                                const cleanOpt = opt.replace(/^\$|\$/g, '');
                                return `
                                    <div class="mwq-option" onclick="selectMWQOption(${index}, ${blankIndex}, '${opt}')">
                                        ${cleanOpt}
                                    </div>
                                `;
                            }).join('')}
                        </div>
                    </div>
                    `;
                }
            }
            return part;
        }).join('');
        html += `<div class="mwq-question">${mwqContent}</div>`;
    } else if (q.type === 'ParagraphMCQ') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls>
                        <source src="${q.audio}" type="audio/mpeg">
                        Your browser does not support the audio element.
                    </audio>
                </div>
                `;
        }
        html += `<div class="paragraph">${q.paragraph}</div>`;
        q.questions.forEach((subQ, subIndex) => {
            html += `<div class="sub-question">
                <p>${subQ.question}</p>
                ${subQ.choices.map((choice, i) => `
                    <div class="choice">
                        <label><input type="radio" name="answer_${subIndex}" value="${choice}"
                        ${userAnswers[index]?.[subIndex] === choice ? 'checked' : ''} 
                        onchange="saveAnswer(${index}, this, ${subIndex})"> ${choice}</label>
                    </div>
                `).join('')}
            </div>`;
        });
    } else if (q.type === 'Matching') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        html += `<h2>${q.question}</h2>`;
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls>
                        <source src="${q.audio}" type="audio/mpeg">
                        Your browser does not support the audio element.
                    </audio>
                </div>
                `;
        }
        html += '<div class="matching-question">';
        q.leftItems.forEach((left, i) => {
            const userAnswer = userAnswers[index]?.[i]; // <-- FIXED
        
            html += `
                <div class="matching-pair">
                    <span>${left}</span>
                    <select onchange="saveAnswer(${index}, this, ${i})">
                        <option value="">Select</option>
                        ${q.rightItems.map((right, j) => `
                            <option value="${j}" ${userAnswer === j ? 'selected' : ''}>${right}</option>
                        `).join('')}
                    </select>
                </div>
            `;
        });
        html += '</div>';
    }else if (q.type === 'Cloze') {
        if (q.instruction) { 
            html += `<h2 class="instruction">${q.instruction}</h2>`; 
        }
        
        
        if (q.image) {
            html += `<img src="${q.image}" alt="Question Image" class="question-image">`;
        }
        if (q.audio) {
            html += `
                <div class="audio-container">
                    <audio id="question-audio" controls controlsList="nodownload">
                        <source src="${q.audio}" type="audio/mpeg">
                        Your device does not support the audio element.
                    </audio>
                </div>
            `;
        }
        
        // Wrap Cloze content in a div for consistency and clarity
        let clozeContent = '<div class="cloze-question">';
        let parts = q.question.split(/\{(\d+):([^:]+):([^}]+)\}/g);
        let htmlParts = [];
        let inputIndex = 0;
        for (let i = 0; i < parts.length; i++) {
            if (i % 4 === 0) {
                htmlParts.push(parts[i]);
            } else if (i % 4 === 1) {
                // Skip the position number
                continue;
            } else if (i % 4 === 2) {
                const type = parts[i];
                const optionsAndAnswer = parts[i + 1];
                let options = [];
                let correctAnswer = '';
                if (type === 'text') {
                    correctAnswer = optionsAndAnswer;
                    const userAnswer = userAnswers[index]?.[inputIndex] || '';
                    htmlParts.push(`<input type="text" class="cloze-input" data-input-index="${inputIndex}" value="${userAnswer}" onchange="saveAnswer(${index}, this, ${inputIndex})">`);
                } else if (type === 'dropdown') {
                    const [optionsString, answer] = optionsAndAnswer.split(':');
                    options = optionsString.split(',');
                    correctAnswer = answer;
                    const userAnswer = userAnswers[index]?.[inputIndex] || '';
                    let dropdownOptions = `<option value="">Select</option>`;
                    dropdownOptions += options.map(opt => `<option value="${opt}" ${userAnswer === opt ? 'selected' : ''}>${opt}</option>`).join('');
                    htmlParts.push(`<select class="cloze-dropdown" data-input-index="${inputIndex}" onchange="saveAnswer(${index}, this, ${inputIndex})">${dropdownOptions}</select>`);
                }
                inputIndex++;
                i++; // Skip the optionsAndAnswer part since we’ve processed it
            }
        }
        clozeContent += htmlParts.join('') + '</div>';
        html += clozeContent;
    }
    
    // Update the question container
    const questionContainer = document.getElementById('question-container');
    questionContainer.innerHTML = html;

    // Apply specific font size for MWQ questions
    if (q.type === 'MWQ') {
        const mwqElement = questionContainer.querySelector('.mwq-question');
        if (mwqElement) {
            mwqElement.style.fontSize = '18px';
        }
    }

    // Render MathJax for MWQ and Mathematics questions
    if ((currentSubject === 'Mathematics'||'Chemistry'||'Physics'||'Biology'||'Economics') && typeof MathJax !== 'undefined') {
        MathJax.typesetPromise([questionContainer])
            .catch(err => console.log('MathJax typesetting error:', err));
    }

    // Apply the Pyidaungsu font and font size for Myanmar subject
    if (currentSubject === 'Myanmar'||'Economics') {
        questionContainer.classList.add('myanmar-font');
        questionContainer.style.fontSize = '16px';
    } else {
        questionContainer.classList.remove('myanmar-font');
        questionContainer.style.fontSize = '18px';
    }

    // Toggle navigation button visibility
    const prevBtn = document.getElementById('prev-btn');
    const nextBtn = document.getElementById('next-btn');
    const submitBtn = document.getElementById('submit-btn');
    prevBtn.style.visibility = index === 0 ? 'hidden' : 'visible';
    nextBtn.style.display = index === quizQuestions.length - 1 ? 'none' : 'inline-block';
    submitBtn.style.display = index === quizQuestions.length - 1 ? 'inline-block' : 'none';

    toggleDrawerButton();
}
window.displayQuestion = displayQuestion;


function toggleDropdown(element) {
    const options = element.nextElementSibling;
    const allOptions = document.querySelectorAll('.dropdown-options');

    allOptions.forEach(opt => {
        if (opt !== options) opt.style.display = 'none';
    });

    options.style.display = options.style.display === 'block' ? 'none' : 'block';
}
window.toggleDropdown=toggleDropdown;

