function updateClearChoicesVisibility(index) {
    let clearChoices = document.getElementById('clear-choices');
    let hasSelection = false;

    if (quizQuestions[index].type === 'TF' || quizQuestions[index].type === 'MCQ') {
        let checkedInputs = document.querySelectorAll(`input[name="answer_${index}"]:checked`);
        hasSelection = checkedInputs.length > 0;
    } else if (quizQuestions[index].type === 'MWQ') {
        hasSelection = userAnswers[index] && userAnswers[index].some(val => val !== "Select");
    } else if (quizQuestions[index].type === 'SA' || quizQuestions[index].type === 'Essay') {
        hasSelection = userAnswers[index] && userAnswers[index].trim() !== "";
    } else if (quizQuestions[index].type === 'Matching') {
        hasSelection = userAnswers[index] && userAnswers[index].some(val => val !== null && val !== "");
    } else if (quizQuestions[index].type === 'Cloze') {
        hasSelection = userAnswers[index] && userAnswers[index].some(val => val !== "");
    } 

    if (hasSelection) {
        clearChoices.style.display = 'block';
        clearChoices.onclick = () => clearAnswer(index);
    } else {
        clearChoices.style.display = 'none';
    }
}
window.updateClearChoicesVisibility =updateClearChoicesVisibility;

