function startTimer() {
    const isVisible =
        window.AndroidPreference?.isTimerVisible?.() ?? true; // fallback to true

    // If native timer overlay is available
    if (window.NativeTimerOverlay?.start) {
        if (isVisible) {
            // Start native timer and show native view
            NativeTimerOverlay.start(7200); // 2 hours(7200)
            document.getElementById('timer').style.visibility = 'hidden';
        } else {
            console.log("Timer is hidden — native timer will run silently");
            NativeTimerOverlay.start(7200); // Still start it, but Java will not show the view
        }
    } else {
        // Fallback: use HTML-based timer
        console.warn("NativeTimerOverlay not available. Using fallback timer.");
        const timerElement = document.getElementById('timer');
        timerElement.style.visibility = isVisible ? 'visible' : 'hidden';
        startFallbackTimer(isVisible);
    }
}

window.startTimer =startTimer;


function startFallbackTimer(autoSubmit = true) {
    let timeLeft = 7200;
    const timerElement = document.getElementById('timer');

    timerInterval = setInterval(() => {
        timeLeft--;
        const hours = Math.floor(timeLeft / 3600);
        const minutes = Math.floor((timeLeft % 3600) / 60);
        const seconds = timeLeft % 60;

        timerElement.textContent = `Time Left: ${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;

        if (timeLeft <= 300) {
            timerElement.classList.add('blinking');
        } else {
            timerElement.classList.remove('blinking');
        }

        if (timeLeft <= 0) {
            clearInterval(timerInterval);
            if (autoSubmit) {
                submitQuiz(true);
            } else {
                console.log("Time up, but auto-submit is disabled.");
            }
        }
    }, 1000);
}

function stopTimer() {
    if (window.NativeTimerOverlay?.stop) {
        NativeTimerOverlay.stop();
    } else if (window.timerInterval) {
        clearInterval(timerInterval);
    }
}


