function calculateResult() {
    let totalScore = 0, maxScore = 0, correctCount = 0;
    let questionStatus = [];
    quizQuestions.forEach((q, i) => {
        let marks = Number(q.marks) || 0;
        if (q.type === 'ParagraphMCQ') {
            marks = q.questions.reduce((sum, subQ) => sum + (Number(subQ.marks) || 0), 0);
        } else if (!Number.isFinite(marks)) {
            console.warn(`Invalid marks for question ${i}: ${q.marks}, defaulting to 0`);
            marks = 0;
        }
        maxScore += marks;
        console.log(`Processing Q${i} (type: ${q.type}, marks: ${marks})`);
        console.log(`userAnswers[${i}]:`, userAnswers[i]);

        if (q.type === 'TF') {
            if (userAnswers[i] === String(q.answer)) {
                totalScore += marks;
                correctCount++;
                questionStatus[i] = 'correct';
            } else if (userAnswers[i] !== String(q.answer)) {
                questionStatus[i] = 'incorrect';
            } else {
                questionStatus[i] = 'not-answered';
            }
        } else if (q.type === 'MCQ') {
            let userAns = userAnswers[i] || (q.multiple ? [] : '');
            let correctAns = Array.isArray(q.answer) ? q.answer : [q.answer];
        
            if (q.multiple) {
                if (userAns.length > 0) {
                    let correctSelections = userAns.filter(a => correctAns.includes(a)).length;
                    let incorrectSelections = userAns.filter(a => !correctAns.includes(a)).length;
        
                    if (correctSelections > 0 && incorrectSelections === 0 && correctSelections === correctAns.length) {
                        totalScore += marks;
                        correctCount++;
                        questionStatus[i] = 'correct';
                    } else if (correctSelections > 0 && incorrectSelections > 0) {
                        questionStatus[i] = 'partially-correct';
                    } else {
                        questionStatus[i] = 'incorrect';
                    }
                } else {
                    questionStatus[i] = 'not-answered';
                }
            } else {
                if (userAns === correctAns[0]) {
                    totalScore += marks;
                    correctCount++;
                    questionStatus[i] = 'correct';
                } else if (userAns !== '') {
                    questionStatus[i] = 'incorrect';
                } else {
                    questionStatus[i] = 'not-answered';
                }
            }
        } else if (q.type === 'MWQ') {
            let userAns = Array.isArray(userAnswers[i]) ? userAnswers[i] : Array(q.blanks.length).fill('');
            let sortedBlanks = [...q.blanks].sort((a, b) => a.position - b.position);
            let correctBlanks = 0;
            sortedBlanks.forEach((b, j) => {
                let userVal = userAns[j] || '';
                let correctVal = b.answer || '';
                if (userVal === correctVal) {
                    correctBlanks++;
                }
            });
        
            totalScore += (correctBlanks / sortedBlanks.length) * marks; // Partial scoring
        
            if (correctBlanks === sortedBlanks.length) {
                correctCount++;
                questionStatus[i] = 'correct';
            } else if (correctBlanks > 0) {
                questionStatus[i] = 'partially-correct';
            } else {
                questionStatus[i] = userAns.some(ans => ans !== '') ? 'incorrect' : 'not-answered';
            }
        } else if (q.type === 'ParagraphMCQ') {
            let subCorrect = 0;
            let userAns = Array.isArray(userAnswers[i]) ? userAnswers[i] : Array(q.questions.length).fill('');
            
            q.questions.forEach((subQ, subI) => {
                let correctAns = Array.isArray(subQ.answer) ? subQ.answer[0] : subQ.answer;
                if ((userAns[subI] || '') === (correctAns || '')) {
                    subCorrect++;
                }
            });
        
            totalScore += (subCorrect / q.questions.length) * marks; // Partial scoring
        
            if (subCorrect === q.questions.length) {
                correctCount++;
                questionStatus[i] = 'correct';
            } else if (subCorrect > 0) {
                questionStatus[i] = 'partially-correct';
            } else {
                questionStatus[i] = userAns.some(ans => ans !== '') ? 'incorrect' : 'not-answered';
            }
        } else if (q.type === 'SA') {
            let userAns = userAnswers[i] || '';
            let isCorrect = Array.isArray(q.correctAnswers) && q.correctAnswers.some(ans => {
                if (ans.includes('*')) {
                    let regexStr = '^' + ans.replace(/[.*+?^${}()|[\]\\]/g, '\\$&').replace(/\*/g, '.*') + '$';
                    let regex = new RegExp(regexStr);
                    return q.caseSensitive ? regex.test(userAns) : regex.test(userAns.toLowerCase());
                }
                return q.caseSensitive ? userAns === ans : userAns.toLowerCase() === ans.toLowerCase();
            });
        
            if (isCorrect) {
                totalScore += marks;
                correctCount++;
                questionStatus[i] = 'correct';
            } else {
                questionStatus[i] = userAns !== '' ? 'incorrect' : 'not-answered';
            }
        } else if (q.type === 'Matching') {
            const userAns = Array.isArray(userAnswers[i]) ? userAnswers[i] : []; // Ensure it's an array
const correctAns = Array.isArray(q.correctMatches) ? q.correctMatches : []; // Ensure correctMatches is an array

let correctMatches = 0;

// Debugging log for correct matches
console.log(`Matching question ${i} - correctMatches:`, correctAns);
console.log(`userAns:`, userAns);

// Loop through each left item and check if the user answer matches the correct answer
for (let j = 0; j < correctAns.length; j++) {
    if (Number(userAns[j]) === Number(correctAns[j])) {
        correctMatches++;
    }
}

// Avoid division by zero
if (correctAns.length > 0) {
    totalScore += (correctMatches / correctAns.length) * marks;
} else {
    console.warn(`No correct answers for Matching question ${i}. Skipping score calculation.`);
}

if (correctMatches === correctAns.length) {
    correctCount++;
    questionStatus[i] = 'correct';
} else if (correctMatches > 0) {
    questionStatus[i] = 'partially-correct';
} else {
    questionStatus[i] = userAns.length > 0 ? 'incorrect' : 'not-answered';
}

        } else if (q.type === 'Cloze') {
            let userAns = Array.isArray(userAnswers[i]) ? userAnswers[i] : [];
            let matches = Array.from(q.question.matchAll(/\{(\d+):([^:]+):([^}]+)\}/g));
            let correctCountCloze = 0;
            
            if (matches.length === 0) {
                questionStatus[i] = 'not-answered';
            } else {
                let idx = 0;
                for (let match of matches) {
                    let type = match[2];
                    let correct = type === 'dropdown' ? match[3].split(':').pop() : match[3];
                    if ((userAns[idx] || '') === (correct || '')) correctCountCloze++;
                    idx++;
                }
        
                totalScore += (correctCountCloze / matches.length) * marks; // Partial scoring
        
                if (correctCountCloze === matches.length) {
                    correctCount++;
                    questionStatus[i] = 'correct';
                } else if (correctCountCloze > 0) {
                    questionStatus[i] = 'partially-correct';
                } else {
                    questionStatus[i] = userAns.length > 0 ? 'incorrect' : 'not-answered';
                }
            }
        }        
    });
    window.questionStatus = questionStatus;
    let percentage = maxScore > 0 ? (totalScore / maxScore) * 100 : 0;
    let timeUsed = (Date.now() - startTime) / 1000;
    let bestScore = Number(localStorage.getItem(`bestScore_${currentSubject}`)) || 0;
    if (totalScore > bestScore) {
        bestScore = totalScore;
        localStorage.setItem(`bestScore_${currentSubject}`, totalScore);
    }

    console.log(`Final Scores - totalScore: ${totalScore}, maxScore: ${maxScore}, correctCount: ${correctCount}, bestScore: ${bestScore}`);
    document.getElementById('best-score').textContent = `${bestScore}`; // Best score across all attempts
    document.getElementById('current-score').textContent = `${totalScore}`; // Current attempt
    document.getElementById('time-used').textContent = `${Math.floor(timeUsed / 60)}m ${Math.floor(timeUsed % 60)}s`;
    document.getElementById('percentage').textContent = `${percentage.toFixed(2)} out of 100.00`;
}
window.calculateResult = calculateResult;

