let initialDistance = null;
let baseFontSizes = {}; // Store initial font sizes
let isPinching = false; // Track if pinch gesture is active

document.addEventListener("DOMContentLoaded", () => {
    document.querySelectorAll(`
        h1, h2, p, button, input, select, textarea, label,
        .instruction, .choice, .mwq-question, .mwq-selected, .mwq-options, .mwq-option,
        .paragraph, .sub-question, .matching-pair, .matching-pair span, .matching-pair select,
        .cloze-dropdown, .mwq-selected-text, .cloze-question, .feedback
    `).forEach(el => {
        if (!el.classList.contains("no-zoom") && !el.closest("#drawer-items")) {  
            baseFontSizes[el] = parseFloat(window.getComputedStyle(el).fontSize);
        }
    });

});

document.addEventListener("touchstart", (event) => {
    if (event.touches.length === 2) {
        initialDistance = getPinchDistance(event.touches);
        isPinching = true; // Enable pinch tracking
        event.preventDefault(); // Prevent default zooming
    }
}, { passive: false });

document.addEventListener("touchmove", (event) => {
    if (event.touches.length === 2 && initialDistance) {
        let newDistance = getPinchDistance(event.touches);
        let scale = newDistance / initialDistance;

        document.querySelectorAll(`
            h1, h2, p, button, input, select, textarea, label,
            .instruction, .choice, .mwq-question, .mwq-selected, .mwq-options, .mwq-option,
            .paragraph, .sub-question, .matching-pair, .matching-pair span, .matching-pair select,
            .cloze-dropdown, .mwq-selected-text, .cloze-question, .feedback
        `).forEach(el => {
            if (!el.classList.contains("no-zoom") && !el.closest("#drawer-items")) {  
                let newSize = Math.max(12, Math.min(30, baseFontSizes[el] * scale)); // Limit font size
                el.style.fontSize = newSize + "px";
            }
        });

        event.preventDefault(); // Prevent page zooming
    }
}, { passive: false });

document.addEventListener("touchend", () => {
    if (initialDistance) {
        initialDistance = null;
        isPinching = false; // Reset pinch tracking

        document.querySelectorAll(`
            h1, h2, p, button, input, select, textarea, label,
            .instruction, .choice, .mwq-question, .mwq-selected, .mwq-options, .mwq-option,
            .paragraph, .sub-question, .matching-pair, .matching-pair span, .matching-pair select,
            .cloze-dropdown, .mwq-selected-text, .cloze-question, .feedback
        `).forEach(el => {
            if (!el.classList.contains("no-zoom") && !el.closest("#drawer-items")) {  
                baseFontSizes[el] = parseFloat(window.getComputedStyle(el).fontSize);
            }
        });
    }
});

document.addEventListener("touchmove", (event) => {
    // Allow scrolling if not pinching
    if (!isPinching) {
        return;
    }
    
    event.preventDefault(); // Only prevent when pinching
}, { passive: false });

function getPinchDistance(touches) {
    let dx = touches[0].clientX - touches[1].clientX;
    let dy = touches[0].clientY - touches[1].clientY;
    return Math.sqrt(dx * dx + dy * dy);
}


