function showSummaryScreen() {
    document.getElementById('main-screen').style.display = 'none';
    document.getElementById('quiz-screen').style.display = 'none';
    document.getElementById('result-screen').style.display = 'none';
    document.getElementById('review-screen').style.display = 'none';
    document.getElementById('summary-screen').style.display = 'block';

    const table = document.getElementById('summary-table');
    table.innerHTML = '';

    let html = `
        <table>
            <thead>
                <tr>
                    <th>Question</th>
                    <th>Status</th>
                </tr>
            </thead>
            <tbody>
    `;

    quizQuestions.forEach((question, i) => {
        let userAnswer = userAnswers[i] ?? null;
        let expectedLength = question.type === 'MWQ' ? question.blanks.length : 
                           (question.type === 'ParagraphMCQ' ? question.questions.length : 
                           (question.type === 'Matching' ? question.leftItems.length :
                           (question.type === 'Cloze' ? (question.question.match(/\{(\d+):/g) || []).length :
                           (Array.isArray(question.answer) ? question.answer.length : 1))));

        let answered = false;
        let partiallyAnswered = false;

        if (question.type === 'MWQ' || question.type === 'ParagraphMCQ' || question.type === 'Matching' || question.type === 'Cloze') {
            if (userAnswer && Array.isArray(userAnswer)) {
                let filledCount = userAnswer.filter(val => val !== "" && val !== null && val !== undefined).length;
                answered = filledCount === expectedLength;
                partiallyAnswered = filledCount > 0 && filledCount < expectedLength;
            }
        } else if (question.type === 'MCQ') {
            if (question.multiple) {
                if (userAnswer && Array.isArray(userAnswer)) {
                    let selectedCount = userAnswer.length;
                    answered = selectedCount === expectedLength;
                    partiallyAnswered = selectedCount > 0 && selectedCount < expectedLength;
                }
            } else {
                answered = userAnswer !== null && userAnswer !== "";
                partiallyAnswered = false;
            }
        } else if (question.type === 'TF') {
            answered = userAnswer === 'TRUE' || userAnswer === 'FALSE';
            partiallyAnswered = false;
        } else if (question.type === 'SA') {
            answered = userAnswer && userAnswer.trim() !== "";
            partiallyAnswered = false;
        }

        let status = answered ? 'answered' : (partiallyAnswered ? 'partially-answered' : 'not-answered');
        let statusLabel = '';

        switch (status) {
            case 'answered':
                statusLabel = 'Answer saved';
                break;
            case 'partially-answered':
                statusLabel = 'Incomplete answer';
                break;
            case 'not-answered':
            default:
                statusLabel = 'Not yet answered';
        }

        html += `
            <tr class="${status}">
                <td><button onclick="goToQuestionFromSummary(${i})">${i + 1}</button></td>
                <td>${statusLabel}</td>
            </tr>
        `;
    });

    html += `</tbody></table>`;
    table.innerHTML = html;
    // update the timer
    updateNativeTimerByScreen();
}


